/* Formatear el campo de importe con 2 decimales */
update aux_h_cobranzas_cascada_afip
set importe_aplicado_cobranza = importe_aplicado_cobranza / 100

/* Actualizar tipo entidad */
update aux_h_cobranzas_cascada_afip
set id_tipo_entidad = 'E'

/* Actualizar signo de importe aplicado */
update aux_h_cobranzas_cascada_afip
set importe_aplicado_cobranza = importe_aplicado_cobranza * -1
where signo = '-'

/* Borra la tabla antes de Llenarla*/

Truncate table  H_COBRANZAS_CASCADA_AFIP


/* Insertar los registros en H_COBRANZAS_CASCADA_AFIP */

insert h_Cobranzas_cascada_Afip
select  id_periodo, id_obra_social, id_empresa, id_cuit_empresa_cobranza, id_periodo_ultima_aplicacion, importe_aplicado_cobranza, id_tipo_entidad from aux_h_cobranzas_cascada_Afip

DELETE H_COBRANZAS_CASCADA_AFIP WHERE id_periodo_ultima_aplicacion < id_periodo
