---- Se crea la temporal con los mail que estan en el excel pero no en la tabla empleado

select distinct email 
into #temp
from  Consultas_DW.dbo.aux_h_info_evaluaciones_playvox A
where email collate SQL_Latin1_General_CP850_CI_AI  not in (select email_empresa from rrhh.rrhh.dbo.empleado )

---- Se envia un mail si existe algun caso inconsistente.

if (select COUNT(email) from #temp) > 0
begin 
	EXEC msdb.dbo.sp_send_dbmail
				@profile_name = 'Soporte Microstrategy',
				@recipients = ' nicolas.borio@sancorsalud.com.ar;  juanfrancisco.trinchieri@sancorsalud.com.ar',
				@subject = 'Evaluaciones Playvox - Mail que no ingresan',
				@body = 'Se detectaron las siguientes casos de mail que no estan ingresando:

				',
				@query = '--- Genera la tabla temp con los email que estan en el excel y no en la tabla empleado
select distinct email 
into #temp
from  Consultas_DW.dbo.aux_h_info_evaluaciones_playvox A
where email collate SQL_Latin1_General_CP850_CI_AI  not in (select email_empresa from rrhh.rrhh.dbo.empleado )

--- Muestra todos los registros por usuario en la h_evaluaciones_playvox que salen de la temporal creada en el paso anterior
select distinct nombre_usuario_playvox,   email from   Consultas_DW.dbo.aux_h_info_evaluaciones_playvox a
where email in ( select email from #temp)
',
				@query_result_separator = '|',
				@execute_query_database = 'DW_SALUD';
end
