/* PERSONAS / CAPITAS - */
DECLARE @PERIODO INT set @PERIODO = ?
TRUNCATE TABLE Consultas_DW.dbo.AUX_H_PERSONAS_CAPITAS_SALUD
/* INSERTA en Consultas_DW.dbo.AUX_H_PERSONAS_CAPITAS_SALUD desde la TMP_IND_CONS_PERSONAS_CAPITAS. Hace un control con Stock Tablero mediante el JOIN con la H_COMER_STOCK_AFILIA */
INSERT INTO Consultas_DW.dbo.AUX_H_PERSONAS_CAPITAS_SALUD (Id_Capita, Id_Periodo, Id_Persona, Afiliado, Subnumero, Secuencia, Id_Producto, Id_SubProducto, Id_plan_Producto, id_localidad_capita) SELECT C.Id_Capita, C.Id_Periodo, C.Id_Persona, C.Afiliado, C.Subnumero, C.Secuencia, C.Id_Producto, C.Id_SubProducto, C.Id_plan_Producto, C.id_localidad FROM Consultas_DW.dbo.TMP_IND_CONS_PERSONAS_CAPITAS C JOIN DW_SALUD.dbo.D_CAPITAS_SALUD S on S.id_capita = C.id_capita WHERE C.id_periodo = @PERIODO /* Es igual al ultimo Periodo de Afiliados. */ and (SUBSTRING(S.fecha_vigencia_hasta,1,6) >= @PERIODO or S.fecha_vigencia_hasta = 0) /* Asegura que las capitas estén en el periodo procesado. */ --and S.id_capita_salud = 1 /* Filtro Clave, debe ser solo salud. En la D_CAPITAS_SALUD hay de todo tipo de capitas pero las de salud las filtramos con este campo. */ GROUP BY C.Id_Capita, C.Id_Periodo, C.Id_Persona, C.Afiliado, C.Subnumero, C.Secuencia, C.Id_Producto, C.Id_SubProducto, C.Id_plan_Producto, C.id_localidad

/* Se actualizan todas las bases pero lo unico que interesa son las personas. */
/* Actualiza D_PRESTACION para identificar los RUBROS / PARA CONSUMOS... */
/* El siguiente script tambien se ejecuta en Data de Consumos paquete D_PRESTACION, paso "Actualiza id_rubro_indicador de consumo y id_prestacion_homologada". Aqui se ejecuta también por precaución al delay entre la ejecución de Consumos e Indicadores */
/* Inserta prestaciones no existentes en D_PRESTACION desde la tabla de Prestaciones por Rubro del AS */ INSERT INTO d_prestacion ([id_prestacion] ,[id_nomenclador] ,[de_codigo_prestacion] ,[de_prestacion] ,[id_capitulo] ,[id_subcapitulo] ,[id_seccion] ,[id_seguir_prestacion] ,[id_grupo_prestacion] ,[id_subgrupo_prestacion] ,[id_accion_farmacologica] ,[id_servicio_prest] ,[id_catalogada] ,[id_cod_resolucion] ,[id_nivel_capitacion] ,[id_complejidad] ,[id_provision_prest] ,[id_rubro_ind_consumo] ,[id_prestacion_oncologia] ,[id_prestacion_homologada] ,[id_marca_recupero_sur] ,[simulador_convenio] ,[orden_pareto] ,[evolucion_pareto]) SELECT CAST(NMRTNO+NMRCPR as CHAR(10)) COLLATE SQL_Latin1_General_CP850_CI_AI as [id_prestacion] , NMRTNO as [id_nomenclador] , NMRCPR as [de_codigo_prestacion] , 'F/D' as [de_prestacion] , 0 as [id_capitulo] , 0 as [id_subcapitulo] , 0 as [id_seccion] , 'N' as [id_seguir_prestacion] , 99 as [id_grupo_prestacion] , 99 as [id_subgrupo_prestacion] , 9999 as [id_accion_farmacologica] , 99 as [id_servicio_prest] , 'N' as [id_catalogada] , 9 as [id_cod_resolucion] , 0 as [id_nivel_capitacion] , 0 as [id_complejidad] , 0 as [id_provision_prest] , RUI_COD as [id_rubro_ind_consumo] , 'N' as [id_prestacion_oncologia] , 0 as [id_prestacion_homologada] , 0 as [id_marca_recupero_sur] , 0 as [simulador_convenio] , 0 as [orden_pareto] , 0 as [evolucion_pareto] FROM HOST03.HOST03.DESARROLLO.CUP001R WHERE CAST(NMRTNO+NMRCPR as CHAR(10)) COLLATE SQL_Latin1_General_CP850_CI_AI not in (SELECT id_prestacion FROM d_prestacion)
/* Actualiza campo id_rubro_ind_consumo en D_PRESTACION */
UPDATE D_PRESTACION SET id_rubro_ind_consumo = A.RUI_COD FROM D_PRESTACION S JOIN HOST03.HOST03.DESARROLLO.CUP001R A on S.id_prestacion = CAST(NMRTNO+NMRCPR as CHAR(10)) COLLATE SQL_Latin1_General_CP850_CI_AI
-- Actualiza las prestaciones Oncológicas del ME que van por acción farmacológica que no se marcan en el AS400 por Adriana. UPDATE DW_SALUD.dbo.D_PRESTACION SET id_rubro_ind_consumo = 'O' WHERE id_nomenclador in ('ME') /* Nomencladores de Medicamentos */ and id_accion_farmacologica in (642,314,159,41,413,768,55,140,73,280,206,151,508,469,500)
-- Actualiza las prestaciones Especiales del ME que van por acción farmacológica que no se marcan en el AS400 por Adriana. UPDATE DW_SALUD.dbo.D_PRESTACION SET id_rubro_ind_consumo = 'P' WHERE id_nomenclador in ('ME') /* Nomencladores de Medicamentos */ and id_accion_farmacologica in (15,108,210,232,275,374,454,473,475,541,576,597,600,657,708,709,712,794,802,803,829,833,847,874,1003)
-- Actualiza las prestaciones Diabetes del ME que van por acción farmacológica que no se marcan en el AS400 por Adriana.
/* Andrea tiene que pasar las acciones farmacologicas*/ UPDATE DW_SALUD.dbo.D_PRESTACION SET id_rubro_ind_consumo = 'Q' WHERE id_nomenclador in ('ME') /* Nomencladores de Medicamentos */ and id_accion_farmacologica in (299,54,795,780,17,339,793,20,699,749,334,790,355,623,98,103,328)

