INSERT INTO DW_SALUD.dbo.AUX_H_INDICADORES_CONS_CANT_PREST (id_periodo, id_ind_consumo, id_persona, nro_orden, id_tipo_orden, id_producto, id_subproducto, id_plan_producto, id_provincia, id_posicion, id_reintegro, id_sexo, edad_orden, edad_periodo, id_rango_edad, id_acreedor, nro_cursograma, Nro_Lote, nro_secuencia, id_prestacion, id_rubro_ind_consumo, cant_practicas_indicador, id_empresa_PE_ind, id_tipo_entidad_PE_ind, id_marca_PE_ind, id_convenio, id_tipo_entidad_convenio) SELECT id_periodo, @Indicador as id_ind_consumo, S.id_persona, S.nro_orden, S.id_tipo_orden, S.id_producto, S.id_subproducto, S.id_plan_producto, ' ' as id_provincia, 0 as id_posicion, S.id_reintegro, 'X' as id_sexo, S.edad_orden, 0 as edad_periodo, 0 as id_rango_edad, S.id_acreedor, S.nro_cursograma, S.Nro_Lote, S.nro_secuencia, S.id_prestacion, A.id_rubro_ind_consumo, sum(cant_prest_amb_pag) as cant_practicas_indicador ,S.id_empresa_PE_cons as id_empresa_PE_ind, S.id_tipo_entidad_PE_cons as id_tipo_entidad_PE_ind, S.id_marca_PE_cons as id_marca_PE_ind, S.id_convenio, S.id_tipo_entidad_convenio FROM DW_SALUD.dbo.H_ORD_AMB_DET S JOIN DW_SALUD.dbo.D_PRESTACION A on S.id_prestacion = A.id_prestacion JOIN DW_SALUD.dbo.D_PRODUCTOS_ORIGINAL P on P.id_producto_original = S.id_producto JOIN DW_SALUD.dbo.D_PLANES_ORIGINAL PL on PL.id_producto_original = S.id_producto and PL.id_plan_producto_original = S.id_plan_producto WHERE A.id_rubro_ind_consumo in ('O') /* Rubro Rec. Medicamentos Oncológicos en AMB */ and id_clase_producto_original in ('S') and S.id_origen_prestacion in ('A') /* Solo Ambulatorio. */ and id_periodo = @Periodo and S.id_reintegro in ('F','R','C') /* No se tiene en cuenta ajustes. Capitas si pero solo porque en Consumos venian registros de prestaciones como Capita pero eran Facturacion. */ and P.id_segmento_original not in (3) and not exists (select FP.id_producto, FP.id_plan_producto from FILTRO_PLAN_PRODUCTO FP where FP.id_producto = S.id_producto and FP.id_plan_producto = S.id_plan_producto) /* Filtros de Tablero. */ and not ((S.id_producto = 4 and S.id_plan_producto = 'PMO ') /* A demás del filtro de planes (igual a Tablero) aqui tampoco tomamos PMO. */ or (S.id_producto = 35 and S.id_plan_producto = 'PMO ')) /* A demás del filtro de planes (igual a Tablero) aqui tampoco tomamos PMO. */ and S.ccosto1 = 1 GROUP BY S.id_periodo, S.id_persona, S.nro_orden, S.id_tipo_orden, S.id_producto, S.id_subproducto, S.id_plan_producto, S.id_posicion_prestacion, S.id_reintegro, S.edad_orden, S.id_acreedor, S.nro_cursograma, S.Nro_Lote, S.nro_secuencia, S.id_prestacion, A.id_rubro_ind_consumo, S.id_empresa_PE_cons, S.id_tipo_entidad_PE_cons, S.id_marca_PE_cons, S.id_convenio, S.id_tipo_entidad_convenio HAVING sum(cant_prest_amb_pag) > 0
/* Borra las personas que están capitadas en Consultas. */
DELETE DW_SALUD.dbo.AUX_H_INDICADORES_CONS_CANT_PREST FROM DW_SALUD.dbo.AUX_H_INDICADORES_CONS_CANT_PREST P JOIN Consultas_DW.dbo.AUX_H_PERSONAS_CAPITAS_SALUD C on P.id_persona = C.id_persona and P.id_producto = C.id_producto and P.id_plan_producto = C.id_plan_producto and P.id_periodo = C.id_periodo JOIN RUBROS_COBERTURAS_CAPITAS RC on RC.id_capita = C.id_capita and P.id_rubro_ind_consumo = RC.id_rubro_ind_consumo WHERE id_ind_consumo = @Indicador
/* Calcula Sexo, Posicion, Provincia y Edad Periodo*/
UPDATE DW_SALUD.dbo.AUX_H_INDICADORES_CONS_CANT_PREST SET id_sexo = P.id_sexo, id_posicion = LA.id_posicion, id_provincia = LA.id_provincia, id_localidad_indicador_consumo = P.id_localidad_afiliado FROM DW_SALUD.dbo.AUX_H_INDICADORES_CONS_CANT_PREST I JOIN DW_SALUD.dbo.D_PERSONAS P on P.id_persona = I.id_persona JOIN DW_SALUD.dbo.D_LOCALIDADES_AFILIADO LA on LA.id_localidad_afiliado = P.id_localidad_afiliado WHERE id_ind_consumo = @Indicador
/* Marca en el Data de Consumos las practicas tomadas para luego utilizar las metricas de importes desde Consumos que no están a nivel de Indicadores. */
UPDATE H_ORD_AMB_DET set consumo_indicador = 0 FROM H_ORD_AMB_DET C JOIN D_PRESTACION P on C.id_prestacion = P.id_prestacion WHERE id_periodo = @Periodo and P.id_rubro_ind_consumo in ('O') /* Rubro Rec. Medicamentos Oncológicos en AMB */
UPDATE H_ORD_AMB_DET set consumo_indicador = 1 FROM DW_SALUD.dbo.AUX_H_INDICADORES_CONS_CANT_PREST P JOIN H_ORD_AMB_DET C on P.id_periodo = C.id_periodo and P.id_persona = C.id_persona and P.nro_cursograma = C.nro_cursograma and P.Nro_Lote = C.Nro_Lote and P.nro_secuencia = C.nro_secuencia JOIN DW_SALUD.dbo.D_PRODUCTOS_ORIGINAL PR on PR.id_producto_original = P.id_producto JOIN DW_SALUD.dbo.D_PLANES_ORIGINAL PL on PL.id_producto_original = P.id_producto and PL.id_plan_producto_original = P.id_plan_producto JOIN D_PRESTACION PRE on P.id_prestacion = PRE.id_prestacion WHERE id_clase_producto_original in ('S') and P.id_reintegro in ('F','R','C') /* No se tiene en cuenta ajustes. Capitas si pero solo porque en Consumos venian registros de prestaciones como Capita pero eran Facturacion. */ and PR.id_segmento_original not in (3) and id_ind_consumo = @Indicador and C.id_periodo = @Periodo and PRE.id_rubro_ind_consumo in ('O') /* Rubro Rec. Medicamentos Oncológicos en AMB */
