/* Borra las personas que están capitadas en Consultas. */
DELETE DW_SALUD.dbo.AUX_H_INDICADORES_CONS_CANT_AFI FROM DW_SALUD.dbo.AUX_H_INDICADORES_CONS_CANT_AFI P JOIN Consultas_DW.dbo.AUX_H_PERSONAS_CAPITAS_SALUD C on P.id_periodo = C.id_periodo and P.id_persona = C.id_persona and P.id_producto = C.id_producto and P.id_plan_producto = C.id_plan_producto JOIN RUBROS_COBERTURAS_CAPITAS RC on RC.id_capita = C.id_capita WHERE id_ind_consumo = @Indicador and RC.id_rubro_ind_consumo in ('O') /* Rubro Rec. Medicamentos Oncológicos en AMB */
