UPDATE DW_SALUD.dbo.AUX_H_INDICADORES_CONS_CANT_AFI SET id_sexo = P.id_sexo, id_posicion = LA.id_posicion, id_provincia = LA.id_provincia, id_localidad_indicador_consumo = P.id_localidad_afiliado FROM DW_SALUD.dbo.AUX_H_INDICADORES_CONS_CANT_AFI I JOIN DW_SALUD.dbo.D_PERSONAS P on P.id_persona = I.id_persona JOIN DW_SALUD.dbo.D_LOCALIDADES_AFILIADO LA on LA.id_localidad_afiliado = P.id_localidad_afiliado WHERE I.id_ind_consumo = @Indicador

DECLARE @Indicador Int , @Periodo Int, @Fecha Datetime, @Periodo_Anterior Int SET @Indicador = 10 SET @Periodo = (select MAX(id_periodo) from DW_SALUD.dbo.AUX_PERIODO_MES_BORRA_IND_CONSUMOS) SET @Fecha = (select MAX(id_fecha) from D_FECHA where id_periodo = @Periodo ) SET @Periodo_Anterior = (select dbo.PeriodoAnterior(@Fecha))
