/* Calcula Ordenes UNICAS */
DROP TABLE Consultas_DW.dbo.TMP_ORDENES_UNICAS
SELECT @Periodo as id_periodo, @Indicador as id_ind_consumo, S.id_persona, S.nro_orden, S.id_tipo_orden, 0 as id_producto, 0 as id_subproducto, ' ' as id_plan_producto, ' ' as id_provincia, 0 as id_posicion, 'X' as id_reintegro, 'X' as id_sexo, MAX(S.edad_orden) as edad_orden, 0 as edad_periodo, 0 as id_rango_edad, 0 as id_acreedor, 0 as nro_cursograma, 0 as Nro_Lote, 0 as nro_secuencia, ' ' as id_prestacion, 0 as Cantidad_Recetas ,S.id_empresa_PE_cons as id_empresa_PE_ind, S.id_tipo_entidad_PE_cons as id_tipo_entidad_PE_ind, S.id_marca_PE_cons as id_marca_PE_ind, 0 as id_localidad_indicador_consumo, S.id_convenio, S.id_tipo_entidad_convenio INTO Consultas_DW.dbo.TMP_ORDENES_UNICAS FROM DW_SALUD.dbo.H_ORD_AMB_DET S JOIN DW_SALUD.dbo.D_PRODUCTOS_ORIGINAL P on P.id_producto_original = S.id_producto JOIN DW_SALUD.dbo.D_PLANES_ORIGINAL PL on PL.id_producto_original = S.id_producto and PL.id_plan_producto_original = S.id_plan_producto WHERE SUBSTRING(S.id_prestacion,1,2) in ('ME','M1') /* Nomencladores de Medicamentos*/ and id_clase_producto_original in ('S') and S.id_periodo = @Periodo and id_provision_prest in (6) /* Provisión de la Prestacion: Medicamentos. */ and S.id_origen_prestacion in ('A') /* Solo Ambulatorio. */ and S.id_reintegro in ('F','R','C') /* No se tiene en cuenta ajustes. Capitas si pero solo porque en Consumos venian registros de prestaciones como Capita pero eran Facturacion. */ and P.id_segmento_original not in (3) and not exists (select FP.id_producto, FP.id_plan_producto from FILTRO_PLAN_PRODUCTO FP where FP.id_producto = S.id_producto and FP.id_plan_producto = S.id_plan_producto) /* Filtros de Tablero. */ and not ((S.id_producto = 4 and S.id_plan_producto = 'PMO ') /* A demás del filtro de planes (igual a Tablero) aqui tampoco tomamos PMO. */ or (S.id_producto = 35 and S.id_plan_producto = 'PMO ')) /* A demás del filtro de planes (igual a Tablero) aqui tampoco tomamos PMO. */ and S.ccosto1 = 1 GROUP BY S.id_persona, S.nro_orden, S.id_tipo_orden , S.id_empresa_PE_cons, S.id_tipo_entidad_PE_cons, S.id_marca_PE_cons, S.id_convenio, S.id_tipo_entidad_convenio
/* Crea tabla CONSUMOS_MEDICAMENTOS como base para el calculo de todo y finaliza en la AUX_H_INDICADORES_CONS_CANT_PREST */
DROP TABLE Consultas_DW.dbo.CONSUMOS_MEDICAMENTOS
SELECT id_periodo, id_ind_consumo, id_persona, nro_orden, id_tipo_orden, id_producto, id_subproducto, id_plan_producto, id_provincia, id_posicion, id_reintegro, id_sexo, edad_orden, edad_periodo, id_rango_edad, id_acreedor, nro_cursograma, Nro_Lote, nro_secuencia, id_prestacion, COUNT(nro_orden) as Cantidad_Recetas, id_empresa_PE_ind, id_tipo_entidad_PE_ind, id_marca_PE_ind, id_localidad_indicador_consumo, id_convenio, id_tipo_entidad_convenio INTO Consultas_DW.dbo.CONSUMOS_MEDICAMENTOS FROM Consultas_DW.dbo.TMP_ORDENES_UNICAS GROUP BY id_periodo, id_ind_consumo, id_persona, nro_orden, id_tipo_orden, id_producto, id_subproducto, id_plan_producto, id_provincia, id_posicion, id_reintegro, id_sexo, edad_orden, edad_periodo, id_rango_edad, id_acreedor, nro_cursograma, Nro_Lote, nro_secuencia, id_prestacion, Cantidad_Recetas , id_empresa_PE_ind, id_tipo_entidad_PE_ind, id_marca_PE_ind, id_localidad_indicador_consumo, id_convenio, id_tipo_entidad_convenio
/* Actializa y prepara la tabla CONSUMOS_MEDICAMENTOS */
UPDATE Consultas_DW.dbo.CONSUMOS_MEDICAMENTOS set id_periodo = S.id_periodo, id_producto = S.id_producto, id_plan_producto = S.id_plan_producto, id_subproducto = S.id_subproducto, id_reintegro = S.id_reintegro, id_acreedor = S.id_acreedor, nro_cursograma = S.nro_cursograma, Nro_Lote = S.Nro_Lote, nro_secuencia = S.nro_secuencia, id_prestacion = S.id_prestacion FROM DW_SALUD.dbo.H_ORD_AMB_DET S JOIN Consultas_DW.dbo.CONSUMOS_MEDICAMENTOS T on T.id_persona = S.id_persona and T.id_tipo_orden = S.id_tipo_orden and T.nro_orden = S.nro_orden JOIN DW_SALUD.dbo.D_PRODUCTOS_ORIGINAL P on P.id_producto_original = S.id_producto JOIN DW_SALUD.dbo.D_PLANES_ORIGINAL PL on PL.id_producto_original = S.id_producto and PL.id_plan_producto_original = S.id_plan_producto WHERE SUBSTRING(S.id_prestacion,1,2) in ('ME','M1') /* Nomencladores Medicos */ and id_clase_producto_original in ('S') and S.id_periodo = @Periodo and id_provision_prest in (6) /* Provision de Medicamentos */ and S.id_origen_prestacion in ('A') /* Solo Ambulatorio. */ and S.id_reintegro in ('F','R','C') /* No se tiene en cuenta ajustes. Capitas si pero solo porque en Consumos venian registros de prestaciones como Capita pero eran Facturacion. */ and P.id_segmento_original not in (3) and not exists (select FP.id_producto, FP.id_plan_producto from FILTRO_PLAN_PRODUCTO FP where FP.id_producto = S.id_producto and FP.id_plan_producto = S.id_plan_producto) /* Filtros de Tablero. */ and not ((S.id_producto = 4 and S.id_plan_producto = 'PMO ') /* A demás del filtro de planes (igual a Tablero) aqui tampoco tomamos PMO. */ or (S.id_producto = 35 and S.id_plan_producto = 'PMO ')) /* A demás del filtro de planes (igual a Tablero) aqui tampoco tomamos PMO. */ and S.ccosto1 = 1
/* Calcula Sexo, Posicion, Provincia y Edad Periodo*/
UPDATE Consultas_DW.dbo.CONSUMOS_MEDICAMENTOS SET id_sexo = P.id_sexo, id_posicion = LA.id_posicion, id_provincia = LA.id_provincia, id_localidad_indicador_consumo = P.id_localidad_afiliado FROM Consultas_DW.dbo.CONSUMOS_MEDICAMENTOS I JOIN DW_SALUD.dbo.D_PERSONAS P on P.id_persona = I.id_persona JOIN DW_SALUD.dbo.D_LOCALIDADES_AFILIADO LA on LA.id_localidad_afiliado = P.id_localidad_afiliado WHERE id_ind_consumo = @Indicador
/* Inserta de la CONSUMOS_MEDICAMENTOS en la Auxiliar */
INSERT INTO DW_SALUD.dbo.AUX_H_INDICADORES_CONS_CANT_PREST (id_periodo, id_ind_consumo, id_persona, nro_orden, id_tipo_orden, id_producto, id_subproducto, id_plan_producto, id_provincia, id_posicion, id_reintegro, id_sexo, edad_orden, edad_periodo, id_rango_edad, id_acreedor, nro_cursograma, Nro_Lote, nro_secuencia, id_prestacion, cant_practicas_indicador, id_empresa_PE_ind, id_tipo_entidad_PE_ind, id_marca_PE_ind, id_localidad_indicador_consumo, id_convenio, id_tipo_entidad_convenio) SELECT id_periodo, @Indicador as id_ind_consumo, S.id_persona, S.nro_orden, S.id_tipo_orden, S.id_producto, S.id_subproducto, S.id_plan_producto, id_provincia, id_posicion, S.id_reintegro, id_sexo, S.edad_orden, edad_periodo, id_rango_edad, S.id_acreedor, S.nro_cursograma, S.Nro_Lote, S.nro_secuencia, S.id_prestacion, Cantidad_Recetas as cant_practicas_indicador, id_empresa_PE_ind, id_tipo_entidad_PE_ind, id_marca_PE_ind, id_localidad_indicador_consumo, S.id_convenio, S.id_tipo_entidad_convenio FROM Consultas_DW.dbo.CONSUMOS_MEDICAMENTOS S
---- Capitas.........
INSERT INTO DW_SALUD.dbo.AUX_H_INDICADORES_CONS_CANT_AFI (id_periodo, id_ind_consumo, id_persona, id_producto, id_subproducto, id_plan_producto, id_provincia, id_posicion, id_sexo, edad_periodo, id_rango_edad, cant_afiliados_indicador, id_periodo_real , id_empresa_PE_ind, id_tipo_entidad_PE_ind, id_marca_PE_ind, id_convenio, id_tipo_entidad_convenio) SELECT @Periodo as id_periodo, @Indicador as id_ind_consumo, S.id_persona, S.id_producto, S.id_subproducto, S.id_plan_producto, 'X' as id_provincia, 0 as id_posicion, 'X' id_sexo, S.edad_periodo, 0 as id_rango_edad, SUM(S.comer_cant_grupo_activo) as cant_afiliados_indicador, s.id_periodo as id_periodo_real ,S.id_empresa_PE as id_empresa_PE_ind, S.id_tipo_entidad_PE as id_tipo_entidad_PE_ind, S.id_marca_PE as id_marca_PE_ind, S.id_convenio, S.id_tipo_entidad_convenio FROM DW_SALUD.dbo.H_COMER_STOCK_AFILIA S JOIN DW_SALUD.dbo.D_PRODUCTOS P on P.id_producto = S.id_producto JOIN DW_SALUD.dbo.D_PLANES PL on PL.id_producto = S.id_producto and PL.id_plan_producto = S.id_plan_producto WHERE id_periodo = @Periodo_Anterior and id_clase_producto in ('S') and PL.id_subrubro_ley in (1, 2, 3, 5, 6, 7) and not exists (select FP.id_producto, FP.id_plan_producto from FILTRO_PLAN_PRODUCTO FP where FP.id_producto = S.id_producto and FP.id_plan_producto = S.id_plan_producto) /* Filtros de Tablero. */ and not ((S.id_producto = 4 and S.id_plan_producto = 'PMO ') /* A demás del filtro de planes (igual a Tablero) aqui tampoco tomamos PMO. */ or (S.id_producto = 35 and S.id_plan_producto = 'PMO ')) /* A demás del filtro de planes (igual a Tablero) aqui tampoco tomamos PMO. */ GROUP BY S.id_periodo, S.id_persona, S.id_producto, S.id_subproducto, S.id_plan_producto, S.edad_periodo, S.id_empresa_PE, S.id_tipo_entidad_PE, S.id_marca_PE, S.id_convenio, S.id_tipo_entidad_convenio
/* Marca en el Data Comercial las Personas tomadas para luego utilizar la relacion con Stock desde Comercial que no están a nivel de Indicadores. */
-- Aqui no marca nada en Stock ya que no existen Capitas de Medicamentos, solo descarta lo que está en Capitas al 100% que hoy en día es OS y SX. Igualmente al ingresar una capita nueva se debe consultar este servicio para marcar el campo "id_capita_os = 1" en D_CAPITAS_SALUD.
/* Borra las personas que están capitadas en Consultas. */
-- A diferencia de Consumos aqui primero marca en el Data y luego elimina las personas de la auxiliar.
DELETE DW_SALUD.dbo.AUX_H_INDICADORES_CONS_CANT_AFI FROM DW_SALUD.dbo.AUX_H_INDICADORES_CONS_CANT_AFI P JOIN Consultas_DW.dbo.AUX_H_PERSONAS_CAPITAS_SALUD C on P.id_periodo = C.id_periodo and P.id_persona = C.id_persona and P.id_producto = C.id_producto and P.id_plan_producto = C.id_plan_producto JOIN D_CAPITAS_SALUD CA on C.id_capita = CA.id_capita WHERE id_ind_consumo = @Indicador and CA.id_capita_os = 1
/* Calcula Sexo, Posicion y Provincia */
UPDATE DW_SALUD.dbo.AUX_H_INDICADORES_CONS_CANT_AFI SET id_sexo = P.id_sexo, id_posicion = LA.id_posicion, id_provincia = LA.id_provincia, id_localidad_indicador_consumo = P.id_localidad_afiliado FROM DW_SALUD.dbo.AUX_H_INDICADORES_CONS_CANT_AFI I JOIN DW_SALUD.dbo.D_PERSONAS P on P.id_persona = I.id_persona JOIN DW_SALUD.dbo.D_LOCALIDADES_AFILIADO LA on LA.id_localidad_afiliado = P.id_localidad_afiliado WHERE id_ind_consumo = @Indicador
