DECLARE @Indicador Int , @Periodo Int, @Fecha Datetime, @Periodo_Anterior Int SET @Indicador = 31 SET @Periodo = (select MAX(id_periodo) from DW_SALUD.dbo.AUX_PERIODO_MES_BORRA_IND_CONSUMOS) SET @Fecha = (select MAX(id_fecha) from D_FECHA where id_periodo = @Periodo ) SET @Periodo_Anterior = (select dbo.PeriodoAnterior(@Fecha))
