INSERT INTO DW_SALUD.dbo.AUX_H_INDICADORES_CONS_CANT_PREST (id_periodo, id_ind_consumo, id_persona, nro_orden, id_tipo_orden, id_producto, id_subproducto, id_plan_producto, id_provincia, id_posicion, id_reintegro, id_sexo, edad_orden, edad_periodo, id_rango_edad, id_acreedor, nro_cursograma, Nro_Lote, nro_secuencia, id_prestacion, id_rubro_ind_consumo, cant_practicas_indicador, id_empresa_PE_ind, id_tipo_entidad_PE_ind, id_marca_PE_ind, id_convenio, id_tipo_entidad_convenio) SELECT id_periodo, @Indicador as id_ind_consumo, S.id_persona, S.nro_orden, S.id_tipo_orden, S.id_producto, S.id_subproducto, S.id_plan_producto, ' ' as id_provincia, 0 as id_posicion, S.id_reintegro, 'X' as id_sexo, S.edad_orden, 0 as edad_periodo, 0 as id_rango_edad, S.id_acreedor, S.nro_cursograma, S.Nro_Lote, S.nro_secuencia, S.id_prestacion, A.id_rubro_ind_consumo, sum(cant_prest_amb_pag) as cant_practicas_indicador ,S.id_empresa_PE_cons as id_empresa_PE_ind, S.id_tipo_entidad_PE_cons as id_tipo_entidad_PE_ind, S.id_marca_PE_cons as id_marca_PE_ind, id_convenio, id_tipo_entidad_convenio FROM DW_SALUD.dbo.H_ORD_AMB_DET S JOIN DW_SALUD.dbo.D_PRESTACION A on S.id_prestacion = A.id_prestacion JOIN DW_SALUD.dbo.D_PRODUCTOS_ORIGINAL P on P.id_producto_original = S.id_producto JOIN DW_SALUD.dbo.D_PLANES_ORIGINAL PL on PL.id_producto_original = S.id_producto and PL.id_plan_producto_original = S.id_plan_producto WHERE A.id_rubro_ind_consumo in ('T') /* Rubro Odontologia - Operatoria Dental en AMB */ and id_clase_producto_original in ('S') and S.id_origen_prestacion in ('A') /* Solo Ambulatorio. */ and id_periodo = @Periodo and S.id_reintegro in ('F','R','C') /* No se tiene en cuenta ajustes. Capitas si pero solo porque en Consumos venian registros de prestaciones como Capita pero eran Facturacion. */ and P.id_segmento_original not in (3) and not exists (select FP.id_producto, FP.id_plan_producto from FILTRO_PLAN_PRODUCTO FP where FP.id_producto = S.id_producto and FP.id_plan_producto = S.id_plan_pro