DELETE DW_SALUD.dbo.AUX_H_INDICADORES_CONS_CANT_PREST FROM DW_SALUD.dbo.AUX_H_INDICADORES_CONS_CANT_PREST P JOIN Consultas_DW.dbo.AUX_H_PERSONAS_CAPITAS_SALUD C on P.id_persona = C.id_persona and P.id_producto = C.id_producto and P.id_plan_producto = C.id_plan_producto and P.id_periodo = C.id_periodo JOIN RUBROS_COBERTURAS_CAPITAS RC on RC.id_capita = C.id_capita and P.id_rubro_ind_consumo = RC.id_rubro_ind_consumo WHERE id_ind_consumo = @Indicador
