UPDATE H_ORD_AMB_DET set consumo_indicador = 0 FROM H_ORD_AMB_DET C JOIN D_PRESTACION P on C.id_prestacion = P.id_prestacion WHERE id_periodo = @Periodo and P.id_rubro_ind_consumo in ('W') /* Rubro Placas Ocusales Odontologia en AMB */
UPDATE H_ORD_AMB_DET set consumo_indicador = 1 FROM DW_SALUD.dbo.AUX_H_INDICADORES_CONS_CANT_PREST P JOIN H_ORD_AMB_DET C on P.id_periodo = C.id_periodo and P.id_persona = C.id_persona and P.nro_cursograma = C.nro_cursograma and P.Nro_Lote = C.Nro_Lote and P.nro_secuencia = C.nro_secuencia JOIN DW_SALUD.dbo.D_PRODUCTOS_ORIGINAL PR on PR.id_producto_original = P.id_producto JOIN DW_SALUD.dbo.D_PLANES_ORIGINAL PL on PL.id_producto_original = P.id_producto and PL.id_plan_producto_original = P.id_plan_producto JOIN D_PRESTACION PRE on P.id_prestacion = PRE.id_prestacion WHERE id_clase_producto_original in ('S') and P.id_reintegro in ('F','R','C') /* No se tiene en cuenta ajustes. Capitas si pero solo porque en Consumos venian registros de prestaciones como Capita pero eran Facturacion. */ and PR.id_segmento_original not in (3) and id_ind_consumo = @Indicador and C.id_periodo = @Periodo and PRE.id_rubro_ind_consumo in ('W') /* Rubro Placas Ocusales Odontologia en AMB */
