/* Actualiza la información existente en D_ACREEDOR. */
UPDATE D_ACREEDOR SET de_acreedor = a12.de_acreedor, id_provincia = a12.id_provincia, id_localidad = a12.id_localidad, id_posicion_acreedor = a12.id_posicion_acreedor, id_categoria_acreedor = a12.id_categoria_acreedor, de_acreedor_reduc = substring(a12.de_acreedor,1,20), domicilio = a12.domicilio, telefono = a12.telefono, fax = a12.fax, email_principal = a12.email_principal, email_alternativo = a12.email_alternativo, celular = a12.celular, id_provision_acreedor = a12.id_provision_acreedor, id_prestador_estrategico_acreedor = a12.id_prestador_estrategico_acreedor, cuit = a12.cuit, id_especialidad_acreedor = 0, id_clasificacion_acreedor = a12.id_clasificacion_acreedor FROM AUX_D_ACREEDOR a12 JOIN D_ACREEDOR A on A.id_acreedor = a12.id_acreedor and A.id_tipo_prestador = a12.id_tipo_prestador
/* Inserta la información faltante en D_ACREEDOR. */
INSERT INTO D_ACREEDOR (id_acreedor, de_acreedor, de_acreedor_reduc, id_categoria_acreedor, id_provincia, id_localidad, subcodpos, id_posicion_acreedor, id_tipo_prestador, domicilio, telefono, fax, email_principal, email_alternativo, celular, id_provision_acreedor, id_prestador_estrategico_acreedor, cuit, id_especialidad_acreedor, id_clasificacion_acreedor, simulador_convenio, evolucion_pareto, orden_pareto, id_estado_acreedor) SELECT distinct cast(id_acreedor as int) as id_acreedor, de_acreedor, substring(de_acreedor,1,20) as de_acreedor_reduc, id_categoria_acreedor, id_provincia, id_localidad, id_posicion_acreedor as subcodpos, id_posicion_acreedor, id_tipo_prestador, domicilio, telefono, fax, email_principal, email_alternativo, celular, id_provision_acreedor, id_prestador_estrategico_acreedor, cuit, 0 as id_especialidad_acreedor, id_clasificacion_acreedor, 0 as simulador_convenio, 0 as evolucion_pareto, 0 as orden_pareto, 9 as id_estado_acreedor FROM AUX_D_ACREEDOR a11 WHERE a11.id_tipo_prestador in ('P','A') and not EXISTS (SELECT * FROM d_acreedor a12 WHERE a12.id_acreedor = cast(a11.id_acreedor as int) and a12.id_tipo_prestador = a11.id_tipo_prestador)
/* Actualiza la Especialidad por Acreedor. Se le carga la minima que se encuentra. */ SELECT id_prestador, min(id_especialidad) as id_especialidad Into #paso1 from D_PRESTADOR_ESPECIALIDAD WHERE id_especialidad <> 0 GROUP BY id_prestador ORDER BY id_prestador
UPDATE D_ACREEDOR SET id_especialidad_acreedor = a10.id_especialidad FROM D_ACREEDOR T JOIN #paso1 a10 on (t.id_acreedor = a10.id_prestador and t.id_tipo_prestador = 'P')
drop table #paso1
/* Actualiza el Estado del Acreedor */ UPDATE D_ACREEDOR SET id_estado_acreedor = W_PV_ESTA FROM D_ACREEDOR D JOIN HOST03.HOST03.SMPDATD.M124FPROV H on h.W_PV_NPRO = D.id_acreedor WHERE d.id_tipo_prestador = 'P'
/* Actualiza la SubCategoria del Acreedor. */ UPDATE D_ACREEDOR SET [id_subcategoria_prestador] = P.CLASIFICACIONES_ID FROM D_ACREEDOR A JOIN AS400.HOST03.PRESTADORES.PRESTADORES P ON P.ID = A.id_acreedor WHERE CLASIFICACIONES_ID IS NOT NULL and id_tipo_prestador = 'P'
