/* Calculo la lista de prestaciones */
insert into H_MAYORIA_EDAD_DETALLE_CONSUMOS select a11.id_persona id_persona, a11.id_periodo, a13.de_prestacion, sum(a11.cant_prest_amb_acep) Cantidades, sum(a11.imp_pret_amb_pag) Importes from AUX_H_MAYORIA_EDAD M join DW_SALUD.dbo.H_ORD_AMB_DET a11 on (a11.id_persona = M.id_persona) join DW_SALUD.dbo.D_PRODUCTOS a12 on (a11.id_producto = a12.id_producto) join DW_SALUD.dbo.D_PRESTACION a13 on (a11.id_prestacion = a13.id_prestacion) where a12.id_segmento not in (3) and a11.ccosto3 = 1 and M.estado = 'ANALIZAR CONSUMOS' and a11.id_periodo in (select top 36 id_periodo from DW_SALUD.dbo.d_periodo where vigente_consumo = 1 order by id_periodo desc) and not exists (select FPP.id_producto, FPP.id_plan_producto from DW_SALUD.dbo.FILTRO_PLAN_PRODUCTO FPP where FPP.id_producto = a11.id_producto and FPP.id_plan_producto = a11.id_plan_producto) group by a11.id_persona, a11.id_periodo, a13.de_prestacion order by a11.id_persona, a11.id_periodo
insert into H_MAYORIA_EDAD_DETALLE_CONSUMOS select a11.id_persona id_persona, a11.id_periodo, a13.de_prestacion, sum(a11.cant_prest_int_acep) Cantidades, sum(a11.imp_prest_int_pag) Importes from AUX_H_MAYORIA_EDAD M join DW_SALUD.dbo.H_ORD_INT_DET a11 on (a11.id_persona = M.id_persona) join DW_SALUD.dbo.D_PRODUCTOS a12 on (a11.id_producto = a12.id_producto) join DW_SALUD.dbo.D_PRESTACION a13 on (a11.id_prestacion = a13.id_prestacion) where a11.ccosto3 = 1 and a12.id_segmento not in (3) and M.estado = 'ANALIZAR CONSUMOS' and a11.id_periodo in (select top 36 id_periodo from DW_SALUD.dbo.d_periodo where vigente_consumo = 1 order by id_periodo desc) and not exists (select FPP.id_producto, FPP.id_plan_producto from DW_SALUD.dbo.FILTRO_PLAN_PRODUCTO FPP where FPP.id_producto = a11.id_producto and FPP.id_plan_producto = a11.id_plan_producto) group by a11.id_persona, a11.id_periodo, a13.de_prestacion order by a11.id_persona, a11.id_periodo
/* Calculo los Totales por prestaciones */
insert into H_MAYORIA_EDAD_DETALLE_CONSUMOS_TOTALES (id_persona, Cantidad, Importe) select id_persona, sum(Cantidades) as Cantidad, sum(Importes) as Importe from H_MAYORIA_EDAD_DETALLE_CONSUMOS group by id_persona order by id_persona
Update H_MAYORIA_EDAD_DETALLE_CONSUMOS_TOTALES set Apellido = MONO.Apellido, Nombre = MONO.Nombre, Costo_Salud = MONO.Costo_Salud_porcent, Estado = MONO.Estado, id_plan_producto = MONO.id_plan_producto from H_MAYORIA_EDAD_DETALLE_CONSUMOS_TOTALES DET inner join AUX_H_MAYORIA_EDAD MONO on MONO.id_persona = DET.id_persona
Update H_MAYORIA_EDAD_DETALLE_CONSUMOS_TOTALES set Recaudacion = REC.total_recaudacion from H_MAYORIA_EDAD_DETALLE_CONSUMOS_TOTALES DET inner join DW_SALUD.dbo.SumReca REC on REC.id_persona = DET.id_persona
