if exists (select * from sysobjects where name = 'SumReca') begin drop table SumReca end
if exists (select * from sysobjects where name = 'SumConsuAMB') begin drop table SumConsuAMB end
if exists (select * from sysobjects where name = 'SumConsuINT') begin drop table SumConsuINT end
if exists (select * from sysobjects where name = 'Costo_Salud_TOTAL') begin drop table Costo_Salud_TOTAL end
declare @Ultimo_Periodo int set @Ultimo_Periodo = (select max(id_periodo) from H_COMER_STOCK_AFILIA)
/* Actualizo Tabla AUX_H_MAYORIA_EDAD */
update Consultas_DW.dbo.AUX_H_MAYORIA_EDAD set Id_periodo = ST.id_periodo, Fecha_nacimiento = P.fecha_nacimiento, Tipo_documento = P.tipo_documento, Nro_documento = P.Nro_documento, Calle_persona = P.Calle_persona, Dpto_persona = P.Dpto_persona, Nro_puerta_persona = P.Nro_puerta_persona, Caracteristica_te = P.Caracteristica_te, Nro_telefono = P.Nro_telefono, Id_localidad_afiliado = P.id_localidad_afiliado, De_localidad = L.de_localidad, Id_provincia = PR.id_provincia, De_provincia = PR.de_provincia, De_zona_comercial = Z.de_zona_comercial, De_posicion = PO.de_posicion, Edad_periodo = ST.edad_periodo, Id_plan_producto = PL.id_plan_producto, Fecha_ingreso_sancor_salud = ST.fecha_ingreso_sancor_salud, Fecha_ingreso_segmento_comercial = ST.fecha_ingreso_segmento_comercial from Consultas_DW.dbo.AUX_H_MAYORIA_EDAD S INNER JOIN D_PERSONAS P on P.id_persona = S.id_persona INNER JOIN D_LOCALIDADES L on L.id_localidad = P.id_localidad_afiliado INNER JOIN D_PROVINCIA PR on PR.id_provincia = L.id_provincia INNER JOIN D_ZONA_COMERCIAL Z on Z.id_zona_comercial = L.id_zona_comercial INNER JOIN D_POSICION PO on PO.id_posicion = L.id_posicion INNER JOIN H_COMER_STOCK_AFILIA ST on ST.id_persona = P.id_persona INNER JOIN D_PLANES PL on PL.id_plan_producto = ST.id_plan_producto where ST.id_periodo = @Ultimo_Periodo
/* Actualizo SubProducto */
update Consultas_DW.dbo.AUX_H_MAYORIA_EDAD set de_subproducto = SU.de_subproducto FROM Consultas_DW.dbo.AUX_H_MAYORIA_EDAD M INNER JOIN H_COMER_STOCK_AFILIA H on M.id_persona = H.id_persona inner join d_subproducto SU on Su.id_subproducto = H.id_subproducto where H.id_periodo = @Ultimo_Periodo
/* Actualizo Grilla */
update Consultas_DW.dbo.AUX_H_MAYORIA_EDAD set grilla = G.grilla from H_COMER_STOCK_AFILIA S inner join Consultas_DW.dbo.AUX_H_MAYORIA_EDAD M on M.id_persona = S.id_persona inner join d_grillas G on g.grilla = S.grilla and G.id_producto = S.id_producto and G.id_plan_producto = S.id_plan_producto where S.id_periodo = @Ultimo_Periodo
/* Actualizo Descripcion de Grilla */ update Consultas_DW.dbo.AUX_H_MAYORIA_EDAD set de_grilla = G.de_grilla from H_COMER_STOCK_AFILIA S inner join Consultas_DW.dbo.AUX_H_MAYORIA_EDAD M on M.id_persona = S.id_persona inner join d_grillas G on g.grilla = S.grilla and G.id_producto = S.id_producto and G.id_plan_producto = S.id_plan_producto where S.id_periodo = @Ultimo_Periodo
/* Actualizo Periodo Alerta */ update Consultas_DW.dbo.AUX_H_MAYORIA_EDAD set Periodo_Alerta = A.Id_Periodo from Consultas_DW.dbo.AUX_H_MAYORIA_EDAD M inner join H_LLAVES_ALERTAS_AFILIADOS A on A.afiliado = M.afiliado
/* Actualizo Motivo Alertas */ update Consultas_DW.dbo.AUX_H_MAYORIA_EDAD set de_motivo_alerta = MO.de_motivo_alerta from Consultas_DW.dbo.AUX_H_MAYORIA_EDAD M inner join H_LLAVES_ALERTAS_AFILIADOS A on A.afiliado = M.afiliado inner join D_MOTIVOS_ALERTA MO on MO.id_motivo_alerta = A.id_motivo_alerta
/* Actualizo Observaciones Alertas */ update Consultas_DW.dbo.AUX_H_MAYORIA_EDAD set observaciones_alerta = A.observaciones from Consultas_DW.dbo.AUX_H_MAYORIA_EDAD M inner join H_LLAVES_ALERTAS_AFILIADOS A on A.afiliado = M.afiliado
/* Actualizo Titulares */
update Consultas_DW.dbo.AUX_H_MAYORIA_EDAD set Titular = 'SI' from Consultas_DW.dbo.AUX_H_MAYORIA_EDAD M inner join H_COMER_GRUPO_ACTIVO ST on ST.id_persona = M.id_persona and ST.id_plan_producto = M.id_plan_producto inner join d_productos PR on PR.id_producto = ST.id_producto where ST.id_periodo = @Ultimo_Periodo ------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------ update Consultas_DW.dbo.AUX_H_MAYORIA_EDAD set Titular = 'NO' where Titular is null
/* Calculo Afiliado , Subnumero y Secuencia */
UPDATE Consultas_DW.dbo.AUX_H_MAYORIA_EDAD set afiliado = H.afiliado, secuencia = H.secuencia, subnumero = H.subnumero FROM Consultas_DW.dbo.AUX_H_MAYORIA_EDAD M INNER JOIN H_COMER_STOCK_AFILIA H on M.id_persona = H.id_persona where H.id_periodo = @Ultimo_Periodo
/* Calculo Forma de Pago */
update Consultas_DW.dbo.AUX_H_MAYORIA_EDAD set Forma_Pago = PAG.WP7FPA from Consultas_DW.dbo.AUX_H_MAYORIA_EDAD ST INNER JOIN [HOST03].[HOST03].[SMPLIBD].[M20FPAD] FO on ST.afiliado = FO.W20NAF and ST.subnumero = FO.W20SAF INNER JOIN [HOST03].[HOST03].[SMPLIBD].[M07FPAG] PAG on FO.W20NAF = PAG.WP7CTA and FO.W20NPP = PAG.WP7NPA
/* Calculo el Mail */
update Consultas_DW.dbo.AUX_H_MAYORIA_EDAD set mail = MA.W03NRO from Consultas_DW.dbo.AUX_H_MAYORIA_EDAD S LEFT OUTER JOIN [HOST03].[HOST03].[SMPLIBD].[M03FTEL] MA on S.id_persona = MA.W03NPE where MA.W03TIT = 'E'
/* Calculo Costo Salud */
/* Sumo la recaudacion */ select M.id_persona, sum(R.total_recaudacion) as total_recaudacion into SumReca from Consultas_DW.dbo.AUX_H_MAYORIA_EDAD M inner join H_RECAUDACION_DETALLE R on M.id_persona = R.id_persona inner join D_PRODUCTOS PR on PR.id_producto = R.id_producto inner join D_PLANES PL on R.id_producto = PL.id_producto and R.id_plan_producto = PL.id_plan_producto WHERE R.id_periodo in (select top 36 id_periodo from DW_SALUD.dbo.d_periodo where vigente_consumo = 1 order by id_periodo desc) and PR.id_segmento <> 3 and not exists (select FPP.id_producto, FPP.id_plan_producto from FILTRO_PLAN_PRODUCTO FPP where R.id_producto = FPP.id_producto and R.id_plan_producto = FPP.id_plan_producto) and R.rcosto3 = 1 group by M.id_persona
/* Sumo el consumo ambulatorio */ select M.id_persona, sum(R.imp_pret_amb_PAG) as total_consumo_AMB into SumConsuAMB from Consultas_DW.dbo.AUX_H_MAYORIA_EDAD M inner join H_ORD_AMB_DET R on M.id_persona = R.id_persona inner join D_PRODUCTOS PR on PR.id_producto = R.id_producto inner join D_PLANES PL on R.id_producto = PL.id_producto and R.id_plan_producto = PL.id_plan_producto WHERE R.id_periodo in (select top 36 id_periodo from DW_SALUD.dbo.d_periodo where vigente_consumo = 1 order by id_periodo desc) and PR.id_segmento <> 3 and R.ccosto3 = 1 and not exists (select FPP.id_producto, FPP.id_plan_producto from FILTRO_PLAN_PRODUCTO FPP where R.id_producto = FPP.id_producto and R.id_plan_producto = FPP.id_plan_producto) group by M.id_persona
/* Sumo el consumo internaciones */ select M.id_persona, sum(R.imp_prest_int_PAG) as total_consumo_INT into SumConsuINT from Consultas_DW.dbo.AUX_H_MAYORIA_EDAD M inner join H_ORD_INT_DET R on M.id_persona = R.id_persona inner join D_PRODUCTOS PR on PR.id_producto = R.id_producto inner join D_PLANES PL on R.id_producto = PL.id_producto and R.id_plan_producto = PL.id_plan_producto WHERE R.id_periodo in (select top 36 id_periodo from DW_SALUD.dbo.d_periodo where vigente_consumo = 1 order by id_periodo desc) and PR.id_segmento <> 3 and R.ccosto3 = 1 and not exists (select FPP.id_producto, FPP.id_plan_producto from FILTRO_PLAN_PRODUCTO FPP where R.id_producto = FPP.id_producto and R.id_plan_producto = FPP.id_plan_producto) group by M.id_persona
------------------------------------------------------------
/* Pongo en 1 las recaudaciones que estan en 0 para que no tire error en la división */ update SumReca set total_recaudación = 1 where total_recaudación = 0 ------------------------------------------------------------
/* Calculo en una auxiliar el consumo / recaudacion = Costo de Salud */ select (sum(IsNull(A.total_consumo_AMB,0) + IsNull(I.total_consumo_INT,0)) / Sum (IsNull(R.total_recaudación,1))) as costo_salud, M.id_persona into Costo_Salud_TOTAL from Consultas_DW.dbo.AUX_H_MAYORIA_EDAD M left outer join SumReca R on M.id_persona = R.id_persona left outer join SumConsuAMB A on M.id_persona = A.id_persona left outer join SumConsuINT I on M.id_persona = I.id_persona group by M.id_persona ------------------------------------------------------------
/* Pego el valor del Costo de Salud en la tabla */ update Consultas_DW.dbo.AUX_H_MAYORIA_EDAD set costo_salud = CAST(C.costo_salud as decimal(8,2)) from DW_SALUD.dbo.Costo_Salud_TOTAL C inner join Consultas_DW.dbo.AUX_H_MAYORIA_EDAD M on C.id_persona = M.id_persona ------------------------------------------------------------
/* Pongo en 0 los costos de salud que tenian 1 en recaudacion, ya que no es acertado el calculo */ update Consultas_DW.dbo.AUX_H_MAYORIA_EDAD set costo_salud = 0 where id_persona not in (select id_persona from SumReca) ------------------------------------------------------------
/* Multiplico para sacar el porcentaje */ update Consultas_DW.dbo.AUX_H_MAYORIA_EDAD set costo_salud = costo_salud * 100
/* Lleno la columna costo_salud_porcent con la informacion de costo_salud*/ update Consultas_DW.dbo.AUX_H_MAYORIA_EDAD set costo_salud_porcent = costo_salud
/* Concateno la columna costo_salud_porcent con el % */ update Consultas_DW.dbo.AUX_H_MAYORIA_EDAD set costo_salud_porcent = (CAST(costo_salud_porcent as char(8))+'%')
/* Saco Espacios en la concatenacion de Costo_Salud_Porcent */ UPDATE Consultas_DW.dbo.AUX_H_MAYORIA_EDAD SET Costo_Salud_Porcent = REPLACE(Costo_Salud_Porcent, ' ','')
/* Calculo Estado*/ update Consultas_DW.dbo.AUX_H_MAYORIA_EDAD set estado = case WHEN costo_salud > 75 THEN 'ANALIZAR CONSUMOS' WHEN costo_salud <= 75 THEN 'ANALIZAR D.E.S' END FROM Consultas_DW.dbo.AUX_H_MAYORIA_EDAD
/* Actualiza el periodo donde comienza la mayoria de edad */ UPDATE Consultas_DW.dbo.AUX_H_MAYORIA_EDAD SET periodo_mayoria_edad = DW_SALUD.dbo.PeriodoMayoriaEdad(fecha_nacimiento)
