/* Consumos Ambulatorio */ SELECT id_periodo, id_persona, id_plan_producto, max(P.de_prestacion) as de_prestacion, SUM(imp_prest_amb_acep) as importe INTO #TMP_AMB FROM DW_SALUD.dbo.H_ORD_AMB_DET C JOIN DW_SALUD.dbo.D_PRESTACION P on P.id_prestacion = C.id_prestacion where id_producto = 18 and id_periodo in (SELECT MAX(id_periodo) FROM DW_SALUD.dbo.AUX_PERIODO_MES_BORRA) and P.id_nomenclador in ('ME','M1') and C.id_reintegro in ('F','R') and id_persona in (SELECT id_persona FROM DW_SALUD.dbo.H_COMP_GRUPO_ACTIVO WHERE id_producto = 18 GROUP BY id_persona) GROUP BY id_periodo, id_persona, id_plan_producto HAVING SUM(imp_prest_amb_acep) > 500
/* Consumos Internaciones */ SELECT id_periodo, id_persona, id_plan_producto, max(P.de_prestacion) as de_prestacion, SUM(imp_prest_int_acep) as importe INTO #TMP_INT FROM DW_SALUD.dbo.H_ORD_INT_DET C JOIN DW_SALUD.dbo.D_PRESTACION P on P.id_prestacion = C.id_prestacion where id_producto = 18 and id_periodo in (SELECT MAX(id_periodo) FROM DW_SALUD.dbo.AUX_PERIODO_MES_BORRA) and P.id_nomenclador in ('ME','M1') and C.id_reintegro in ('F','R') and id_persona in (SELECT id_persona FROM DW_SALUD.dbo.H_COMP_GRUPO_ACTIVO WHERE id_producto = 18 GROUP BY id_persona) GROUP BY id_periodo, id_persona, id_plan_producto HAVING SUM(imp_prest_int_acep) > 500
/* me fijo si existen personas en INT que no estan en AMB */ /* este control se hace por si se da el caso de que no existan combinaciones entre ambas tablas y no queden campos nulos */ select id_persona into #TMP_PERSONAS from #TMP_INT I where not exists (select id_persona from #TMP_AMB A where A.id_persona = I.id_persona)
if ((select COUNT(*) from #TMP_PERSONAS) > 0) begin /* Si existen casos inserto los de la tabla INT */
INSERT INTO AMPS_PUNTOA (id_periodo, id_persona, plan_producto, de_prestacion, Importe) SELECT I.id_periodo, I.id_persona, I.id_plan_producto, I.de_prestacion, I.importe Importe FROM #TMP_INT I --JOIN #TMP_AMB A on A.id_periodo = I.id_periodo and A.id_persona = I.id_persona --GROUP BY I.id_periodo, I.id_persona, I.id_plan_producto, I.de_prestacion
delete #TMP_INT where id_persona in (select id_persona from #TMP_PERSONAS)
end
INSERT INTO AMPS_PUNTOA (id_periodo, id_persona, plan_producto, de_prestacion, Importe) SELECT A.id_periodo, A.id_persona, A.id_plan_producto, A.de_prestacion, SUM(IsNull(CAST(A.importe AS FLOAT),0)+IsNull(CAST(I.importe as FLOAT),0)) AS Importe FROM #TMP_AMB A FULL OUTER JOIN #TMP_INT I on A.id_periodo = I.id_periodo and A.id_persona = I.id_persona GROUP BY A.id_periodo, A.id_persona, A.id_plan_producto, A.de_prestacion
UPDATE AMPS_PUNTOA SET Nombre = P.Nombre, Apellido = P.Apellido FROM AMPS_PUNTOA A JOIN DW_SALUD.dbo.D_PERSONAS P on A.id_persona = P.id_persona
