/* Calcula las personas AMPS con sus entidades en cada periodo */
SELECT S.id_periodo, S.id_persona, S.id_entidad INTO #Personas18 FROM DW_SALUD.dbo.H_COMP_GRUPO_ACTIVO S JOIN DW_SALUD.dbo.D_ENTIDADES E on S.id_entidad = E.id_entidad WHERE id_producto = 18 GROUP BY S.id_periodo, S.id_persona, S.id_entidad
/* Calcula consumos */
-- ambulatorio SELECT C.id_periodo, C.id_persona, C.id_plan_producto, S.id_entidad, C.id_acreedor, SUM(imp_pret_amb_pag) as Importe INTO #TMP_AMB FROM DW_SALUD.dbo.H_ORD_AMB_DET C JOIN DW_SALUD.dbo.D_PRESTACION P on C.id_prestacion = P.id_prestacion JOIN #Personas18 S on C.id_persona = S.id_persona and C.id_periodo = S.id_periodo where C.id_producto = 18 and C.id_periodo in (SELECT MAX(id_periodo) FROM DW_SALUD.dbo.AUX_PERIODO_MES_BORRA) and C.id_reintegro in ('F','R','C') GROUP BY C.id_periodo, C.id_persona, C.id_plan_producto, S.id_entidad, C.id_acreedor
-- internaciones SELECT C.id_periodo, C.id_persona, C.id_plan_producto, S.id_entidad, C.id_acreedor, SUM(imp_prest_int_pag) as Importe INTO #TMP_INT FROM DW_SALUD.dbo.H_ORD_INT_DET C JOIN DW_SALUD.dbo.D_PRESTACION P on C.id_prestacion = P.id_prestacion JOIN #Personas18 S on C.id_persona = S.id_persona and C.id_periodo = S.id_periodo where C.id_producto = 18 and C.id_periodo in (SELECT MAX(id_periodo) FROM DW_SALUD.dbo.AUX_PERIODO_MES_BORRA) and C.id_reintegro in ('F','R','C') GROUP BY C.id_periodo, C.id_persona, C.id_plan_producto, S.id_entidad, C.id_acreedor
/* me fijo si existen personas en INT que no estan en AMB */ /* este control se hace por si se da el caso de que no existan combinaciones entre ambas tablas y no queden campos nulos */ select id_persona into #TMP_PERSONAS from #TMP_INT I where not exists (select id_persona from #TMP_AMB A where A.id_persona = I.id_persona)
if ((select COUNT(*) from #TMP_PERSONAS) > 0) begin /* Si existen casos inserto los de la tabla INT */
INSERT INTO AMPS_PUNTOF (id_periodo, id_persona, plan_producto, entidad, acreedor, Importe) SELECT I.id_periodo, I.id_persona, I.id_plan_producto, I.id_entidad, I.id_acreedor, I.importe FROM #TMP_INT I --JOIN #TMP_AMB A on A.id_periodo = I.id_periodo and A.id_persona = I.id_persona --GROUP BY I.id_periodo, I.id_persona, I.id_plan_producto, I.de_prestacion
delete #TMP_INT where id_persona in (select id_persona from #TMP_PERSONAS)
end
/* Actualiza AMPS_PUNTOF */
INSERT INTO AMPS_PUNTOF (id_periodo, id_persona, plan_producto, entidad, acreedor, Importe) SELECT A.id_periodo, A.id_persona, A.id_plan_producto, A.id_entidad, A.id_acreedor, SUM(IsNull(A.Importe,0)+IsNull(I.Importe,0)) AS Importe FROM #TMP_AMB A FULL OUTER JOIN #TMP_INT I on A.id_periodo = I.id_periodo and A.id_persona = I.id_persona GROUP BY A.id_periodo, A.id_persona, A.id_plan_producto, A.id_entidad, A.id_acreedor
/* Actualiza Nombre y Apellido */ UPDATE AMPS_PUNTOF SET Nombre = P.Nombre, Apellido = P.Apellido FROM AMPS_PUNTOF A JOIN DW_SALUD.dbo.D_PERSONAS P on A.id_persona = P.id_persona
/* Actualiza Descrioción de entidad a informar */ UPDATE AMPS_PUNTOF SET desc_entidad = P.de_entidad FROM AMPS_PUNTOF A JOIN DW_SALUD.dbo.D_ENTIDADES P on A.entidad = P.id_entidad
/* Actualiza Localidad del Afiliado */ UPDATE AMPS_PUNTOF SET localidad_entidad = O.W16COP from HOST03.HOST03.SMPLIBD.M09FASE E JOIN HOST03.HOST03.SMPLIBD.M05FEMP A ON E.W09NPE = A.W05EMP JOIN HOST03.HOST03.SMPLIBD.M16FDOE O ON A.W05EMP = O.W16EMP JOIN AMPS_PUNTOF H ON E.W09NAS = H.entidad
/* Actualiza Descriopcion c Localidad del Afiliado */ UPDATE AMPS_PUNTOF SET desc_localidad_entidad = P.de_localidad FROM AMPS_PUNTOF A JOIN DW_SALUD.dbo.D_LOCALIDADES P on A.localidad_entidad = P.id_localidad
UPDATE AMPS_PUNTOF SET localidad_entidad = 0, desc_localidad_entidad = 0 where localidad_entidad is null
