/* Calcula las personas AMPS con sus entidades en cada periodo */
SELECT S.id_periodo, S.id_persona, S.id_entidad INTO #Personas18 FROM DW_SALUD.dbo.H_COMP_GRUPO_ACTIVO S WHERE id_producto = 18 GROUP BY S.id_periodo, S.id_persona, S.id_entidad
/* Calcula consumos */
-- ambulatorio SELECT C.id_periodo, C.id_persona, C.id_plan_producto, S.id_entidad, C.id_acreedor, SUM(imp_pret_amb_pag) as Importe INTO #TMP_AMB FROM DW_SALUD.dbo.H_ORD_AMB_DET C JOIN DW_SALUD.dbo.D_PRESTACION P on C.id_prestacion = P.id_prestacion JOIN #Personas18 S on C.id_persona = S.id_persona and C.id_periodo = S.id_periodo where C.id_producto = 18 and C.id_periodo in (SELECT MAX(id_periodo) FROM DW_SALUD.dbo.AUX_PERIODO_MES_BORRA) and C.id_reintegro in ('F','R','C') GROUP BY C.id_periodo, C.id_persona, C.id_plan_producto, S.id_entidad, C.id_acreedor
-- internaciones SELECT C.id_periodo, C.id_persona, C.id_plan_producto, S.id_entidad, C.id_acreedor, SUM(imp_prest_int_pag) as Importe INTO #TMP_INT FROM DW_SALUD.dbo.H_ORD_INT_DET C JOIN DW_SALUD.dbo.D_PRESTACION P on C.id_prestacion = P.id_prestacion JOIN #Personas18 S on C.id_persona = S.id_persona and C.id_periodo = S.id_periodo where C.id_producto = 18 and C.id_periodo in (SELECT MAX(id_periodo) FROM DW_SALUD.dbo.AUX_PERIODO_MES_BORRA) and C.id_reintegro in ('F','R','C') GROUP BY C.id_periodo, C.id_persona, C.id_plan_producto, S.id_entidad, C.id_acreedor
/* me fijo si existen personas en INT que no estan en AMB */ /* este control se hace por si se da el caso de que no existan combinaciones entre ambas tablas y no queden campos nulos */ select id_persona into #TMP_PERSONAS from #TMP_INT I where not exists (select id_persona from #TMP_AMB A where A.id_persona = I.id_persona)
if ((select COUNT(*) from #TMP_PERSONAS) > 0) begin /* Si existen casos inserto los de la tabla INT */
INSERT INTO AMPS_PUNTOI (id_periodo, persona, plan_producto, entidad, acreedor, Importe) SELECT I.id_periodo, I.id_persona, I.id_plan_producto, I.id_entidad, I.id_acreedor, I.importe FROM #TMP_INT I --JOIN #TMP_AMB A on A.id_periodo = I.id_periodo and A.id_persona = I.id_persona --GROUP BY I.id_periodo, I.id_persona, I.id_plan_producto, I.de_prestacion
delete #TMP_INT where id_persona in (select id_persona from #TMP_PERSONAS)
end
/* Actualiza AMPS_PUNTOI */
INSERT INTO AMPS_PUNTOI (id_periodo, persona, plan_producto, entidad, acreedor, Importe) SELECT A.id_periodo, A.id_persona, A.id_plan_producto, A.id_entidad, A.id_acreedor, SUM(IsNull(A.Importe,0)+IsNull(I.Importe,0)) AS Importe FROM #TMP_AMB A FULL OUTER JOIN #TMP_INT I on A.id_periodo = I.id_periodo and A.id_persona = I.id_persona GROUP BY A.id_periodo, A.id_persona, A.id_plan_producto, A.id_entidad, A.id_acreedor
/* Actualiza Descrioción de Entidad a informar */ UPDATE AMPS_PUNTOI SET desc_entidad = P.de_entidad FROM AMPS_PUNTOI A JOIN DW_SALUD.dbo.D_ENTIDADES P on A.entidad = P.id_entidad
/* Actualiza Descrioción de Acreedor a informar */ UPDATE AMPS_PUNTOI SET desc_acreedor = P.de_acreedor, especialidad = P.id_especialidad_acreedor FROM AMPS_PUNTOI A JOIN DW_SALUD.dbo.D_ACREEDOR P on A.acreedor = P.id_acreedor where P.id_tipo_prestador in ('P')
/* Actualiza Especialidad de Acreedor a informar */ UPDATE AMPS_PUNTOI SET desc_especialidad = P.de_especialidad_acreedor FROM AMPS_PUNTOI A JOIN DW_SALUD.dbo.D_ESPECIALIDAD_ACREEDOR P on A.especialidad = P.id_especialidad_acreedor
