Truncate table AUX_H_R331_AFIL_PROVINCIA
Declare @Periodo_Hasta Int, @Periodo_Desde Int Set @Periodo_Hasta = (select max(id_periodo) from D_PERIODO where vigente_consumo = 1 and id_mes in (3,6,9,12)) SET @Periodo_Desde = (select dbo.PeriodosAnteriores(@Periodo_Hasta,3))
/* Inserta las personas con coberturas al 70 y al 100. */ INSERT INTO AUX_H_R331_AFIL_PROVINCIA (id_provincia, id_cobertura, id_orden_provincia, id_entidad_capita, cant_afiliados) SELECT F.id_provincia, id_cobertura, id_orden_provincia, id_entidad_capita, COUNT(distinct F.id_persona) as cant_afiliados from AUX_H_R331 F join D_PERSONAS P on F.id_persona = P.id_persona join D_LOCALIDADES L on P.id_localidad_afiliado = L.id_localidad join D_PROVINCIA PR on PR.id_provincia = L.id_provincia GROUP BY F.id_provincia, id_cobertura, id_orden_provincia, id_entidad_capita
/* Inserta las personas con coberturas al 40 y 70 (que no estén en la tabla AUX_H_R331 ) */ INSERT INTO AUX_H_R331_AFIL_PROVINCIA (id_provincia, id_cobertura, id_orden_provincia, id_entidad_capita, cant_afiliados) SELECT PR.id_provincia, porcentaje_cobertura_stock, id_orden_provincia, 16 as id_entidad_capita, COUNT(distinct F.id_persona) as cant_afiliados from H_COMER_STOCK_AFILIA F join D_PERSONAS P on F.id_persona = P.id_persona join D_LOCALIDADES L on P.id_localidad_afiliado = L.id_localidad join D_PROVINCIA PR on PR.id_provincia = L.id_provincia WHERE F.id_convenio_especial = 7 and id_periodo between @Periodo_Desde and @Periodo_Hasta and F.id_persona not in (select distinct id_persona from AUX_H_R331) GROUP BY PR.id_provincia, porcentaje_cobertura_stock, id_orden_provincia
