DECLARE @Periodo INT SET @Periodo = ? DECLARE @PeriodoMin INT SET @PeriodoMin = (SELECT MIN(CM6PER) from Consultas_DW.dbo.R650) DECLARE @AnioMin INT SET @AnioMin = (SELECT MIN(id_anio) from Consultas_DW.dbo.R650) DECLARE @TrimestreMin INT SET @TrimestreMin = (SELECT MIN(id_trimestre) from Consultas_DW.dbo.R650 where id_anio = @AnioMin)
/* Actualiza trimestre y año */
UPDATE Consultas_DW.dbo.R650 set id_trimestre = P.id_trimestre, id_anio = P.id_anio FROM Consultas_DW.dbo.R650 R JOIN D_PERIODO P on P.id_periodo = R.CM6PER WHERE R.CM6PER = @Periodo
/* Actualiza Entidad de Capita SOLO PARA OS/SX. */
UPDATE Consultas_DW.dbo.R650 SET id_entidad_capita = C.id_entidad_capita FROM H_PERSONAS_CAPITAS_SALUD B JOIN Consultas_DW.dbo.R650 A ON (A.CM6NPE = B.id_persona AND A.CM6PER = B.id_periodo AND A.CM6NAF = B.afiliado AND A.CM6SAF = B.subnumero) JOIN D_CAPITAS_SALUD C on C.id_capita = B.id_capita WHERE C.id_entidad_capita in (1,2,3,9,10,11,17) and CM6PER = @Periodo
UPDATE Consultas_DW.dbo.R650 SET id_entidad_capita = 0 where id_entidad_capita is null and CM6PER = @Periodo

	DECLARE @Periodo INT SET @Periodo = ?

/* Borra periodo a ejecutar. */
	DELETE FROM Consultas_DW.dbo.R650 WHERE CM6PER = @Periodo
