/* Creo la información faltante */
INSERT INTO [DW_SALUD].[dbo].[D_PROMOTOR] ([id_promotor] ,[de_promotor]) SELECT id_promotor, de_promotor FROM AUX_D_PROMOTOR WHERE id_promotor not in (SELECT id_promotor FROM D_PROMOTOR) and id_promotor not in ('JULIET')
--- Actualiza la id_zona_comercial_promotor de la tabla D_Promotor con los datos de as400.
update D_PROMOTOR set id_zona_comercial_promotor = as400.m09coz from [HOST03].[HOST03].[SMPLIBD].[M09FASE] as400, D_PROMOTOR d where d.id_promotor = as400.W09NAS and as400.m09coz <> 0
/* Actualiza id_zona_comercial_promotor desde Cascada Comercial */
Declare @Periodo_Produccion Int SET @Periodo_Produccion = (select PERPRO from HOST03.HOST03.DWHSMPD.DFECPRO)
Update D_PROMOTOR set id_zona_comercial_promotor = h.id_zona_comercial_promotor from D_PROMOTOR D inner join H_CASCADA_COMERCIAL H ON d.id_promotor = H.id_asesor_cascada_comercial where H.id_periodo = @Periodo_Produccion
--- Solucion transitoria hasta tanto cambien en M09FASE las agrupaciones de zonas - Cambio hecho por Liliana
update D_PROMOTOR set id_zona_comercial_promotor = 10 where id_zona_comercial_promotor = 19 update D_PROMOTOR set id_zona_comercial_promotor = 26 where id_zona_comercial_promotor = 39 update D_PROMOTOR set id_zona_comercial_promotor = 28 where id_zona_comercial_promotor = 37 update D_PROMOTOR set id_zona_comercial_promotor = 33 where id_zona_comercial_promotor = 38
/*A raiz de pedido de Gerencia Comercial toda la produccion de Cerro las Rosas se ve dentro de la Produccion de Cordoba */ UPDATE D_PROMOTOR SET id_zona_comercial_promotor = 2 where id_zona_comercial_promotor = 48
/* Actualiza la info de id_supervisor_ultimo_del_promotor */
update D_PROMOTOR set id_supervisor_ultimo_del_promotor = id_asesor_superior_cascada_comercial from D_PROMOTOR PROM inner join H_CASCADA_COMERCIAL CAS ON (PROM.id_promotor = CAS.id_asesor_cascada_comercial) WHERE id_tipo_puesto_superior_cascada_comercial in (3,2,23) and id_periodo = (select MAX(id_periodo) from H_CASCADA_COMERCIAL)
/* si quedo un asesor lider como ultimo supervisor se busca su superior */
update D_PROMOTOR set id_supervisor_ultimo_del_promotor = id_asesor_superior_cascada_comercial from D_PROMOTOR PROM inner join H_CASCADA_COMERCIAL CAS ON (PROM.id_supervisor_ultimo_del_promotor = CAS.id_asesor_cascada_comercial) WHERE id_tipo_puesto_cascada_comercial in (2) and id_periodo = (select MAX(id_periodo) from H_CASCADA_COMERCIAL)
/* Si el promotor por algun motivo no tienen un supervisor, se le asigna el valor por defecto */
update D_PROMOTOR set id_supervisor_ultimo_del_promotor = 99999 from D_PROMOTOR Where id_supervisor_ultimo_del_promotor is null
