select nro_documento,ROW_NUMBER() OVER (ORDER BY id_fecha_carga_trazabilidad DESC) nro_ficha into #temp_ficha from AUX_H_TRAZABILIDAD_FICHAS_PADRON
update AUX_H_TRAZABILIDAD_FICHAS_PADRON set nro_ficha = t.nro_ficha from AUX_H_TRAZABILIDAD_FICHAS_PADRON a inner join #temp_ficha t on a.nro_documento = t.nro_documento
Declare @Periodo_Produccion INT = (select PERPRO from HOST03.HOST03.DWHSMPD.DFECPRO)
insert into H_TRAZABILIDAD_FICHAS (id_periodo , id_fecha_carga_trazabilidad , id_ficha , id_fecha_solicitud , id_fecha_vigencia_trazabilidad , id_tipo_movimiento_trazabilidad , id_subtipo_movimiento_trazabilidad , id_sector_carga , id_localidad_afiliado , id_promotor , id_jefe_equipo , id_supervisor , id_entidad , id_marca_corporativa , id_empresa , id_tipo_entidad , id_factura_afiliado_empresa , id_empresa_factura , id_tipo_entidad_factura , id_comercializadora , id_producto , id_plan_producto , grilla , id_subproducto , cantidad_integrantes , valor_plan , id_unifica , id_afiliacion_principal , cuit_afiliacion_unifica , id_marca_cambio_segmento , id_periodo_produccion , cant_fichas_trazabilidad_dia , id_forma_pago , remuneracion , aporte_monotributo , total_a_pagar , id_marca_cantidad_integrantes , nro_documento , id_marca_documento_repetido , id_marca_informe_trazabilidad , cant_fichas_con_tickets , importe_total_tickets , fecha_pago_tickets , cant_fichas_alta_efectiva , cant_fichas_que_llegaron_a_carga , id_datero , id_asesor_superior_cascada_comercial , id_tipo_puesto_superior_cascada_comercial , id_gerente_zonal , id_gerente_regional , id_asesor_gerente_zonal , id_tipo_puesto_gerente_zonal , id_asesor_gerente_regional , id_tipo_puesto_gerente_regional , id_asesor_lider_cascada_comercial , id_tipo_puesto_asesor_lider_cascada_comercial , id_persona , id_motivo_baja , id_origen_trazabilidad , id_zona_comercial_promotor , id_motivo_error , id_ejecutivo_empresa , id_tipo_puesto_ejecutivo_empresa , porcentaje_ejecutivo, [contar_documentos_distintos] ) select @Periodo_Produccion as id_periodo,cast(cast(id_fecha_carga_trazabilidad as varchar(8))as datetime) , cast(cast(@Periodo_Produccion as varchar(6)) + cast(nro_ficha as varchar(4)) as int) as id_ficha, cast(cast(id_fecha_solicitud as varchar(8))as datetime), cast(cast(id_fecha_vigencia_trazabilidad as varchar(8))as datetime), 1 as id_tipo_movimiento_trazabilidad, 1 as id_subtipo_movimiento_trazabilidad, 999 as id_sector_carga, id_localidad_afiliado, id_promotor, id_jefe_equipo as id_jefe_equipo, 0 as id_supervisor, id_entidad, 3 as id_marca_corporativa, 0 as id_empresa, 'E' as id_tipo_entidad, id_factura_afiliado_empresa, id_empresa, 'E' as id_tipo_entidad_factura, id_comercializadora, id_producto, id_plan_producto, grilla, id_subproducto, 1 as cantidad_integrantes, 0 as valor_plan, 'N' as id_unifica, 'S' as id_afiliacion_principal, 0 as cuit_afiliacion_unifica, 'N' as id_marca_cambio_segmento, @Periodo_Produccion as id_periodo_produccion, 1 as cant_fichas_trazabilidad_dia, id_forma_pago, remuneracion, aporte_monotributo, 0 as total_a_pagar, 1 as id_marca_cantidad_integrantes, nro_documento, 0 as id_marca_documento_repetido, 1 as id_marca_informe_trazabilidad, 0 as cant_fichas_con_tickets, 0 as importe_total_tickets, '1900-01-01 00:00:00.000' as fecha_pago_tickets , 1 as cant_fichas_alta_efectiva, 1 as cant_fichas_que_llegaron_a_carga, id_datero, 0 as id_asesor_superior_cascada_comercial,0 as id_tipo_puesto_superior_cascada_comercial,99 as id_gerente_zonal, 99 as id_gerente_regional, null as id_asesor_gerente_zonal, null as id_ipo_puesto_gerente_zonal, null as id_asesor_gerente_regional, null as id_tipo_puesto_gerente_regional, null as id_asesor_lider_cascada_comercial, null as id_tipo_puesto_asesor_lider_cascada_comercial, id_persona, 0 AS id_motivo_baja, 1 AS id_origen_trazabilidad , 0 as id_zona_comercial_promotor,0 as id_motivo_error ,0 as id_ejecutivo_empresa, 22 as id_tipo_puesto_ejecutivo_empresa,0 as porcentaje_ejecutivo, 0 as [contar_documentos_distintos] from AUX_H_TRAZABILIDAD_FICHAS_PADRON
update H_TRAZABILIDAD_FICHAS set id_marca_informe_trazabilidad = 0 where id_plan_producto = 'PMO' and id_origen_trazabilidad = 1
update H_TRAZABILIDAD_FICHAS set id_persona = d.id_persona from H_TRAZABILIDAD_FICHAS h inner join d_personas d on h.nro_documento = d.nro_documento where id_origen_trazabilidad = 1 and h.id_persona = 0
delete d from D_PERSONAS_TRAZABILIDAD d inner join h_trazabilidad_fichas h on h.id_ficha = d.id_ficha --and h.nro_documento = d.nro_documento where id_origen_trazabilidad = 1 and id_periodo = @Periodo_Produccion and id_marca_informe_trazabilidad = 1
insert into d_personas_trazabilidad select id_ficha,'','', nro_documento from H_TRAZABILIDAD_FICHAS where --id_periodo = @Periodo_Produccion and id_origen_trazabilidad = 1 and id_ficha not in (select id_ficha from d_personas_trazabilidad ) and id_marca_informe_trazabilidad = 1 group by id_ficha,nro_documento
/* Actualización de apellido y nombre en D_PERSONAS_TRAZABILIDAD */
update d_personas_trazabilidad set de_apellido_titular = A84APE, de_nombre_titular = A84NOM from d_personas_trazabilidad d inner join HOST03.HOST03.SMPLIBD.H84FTXT as400 on d.nro_documento = as400.A84DOC inner join H_TRAZABILIDAD_FICHAS H on H.nro_documento = d.nro_documento where H.id_origen_trazabilidad = 1 and H.id_periodo_produccion = @Periodo_Produccion
-- Actualiza el id_factura_afiliado_empresa segun si factura a empresa o afiliado update H_TRAZABILIDAD_FICHAS set id_factura_afiliado_empresa = 'E' from H_TRAZABILIDAD_FICHAS where id_origen_trazabilidad = 1 and id_factura_afiliado_empresa = '' and id_empresa_factura <> 0 and id_periodo = @Periodo_Produccion
update H_TRAZABILIDAD_FICHAS set id_factura_afiliado_empresa = 'A' from H_TRAZABILIDAD_FICHAS where id_origen_trazabilidad = 1 and id_factura_afiliado_empresa = '' and id_empresa_factura = 0 and id_periodo = @Periodo_Produccion
/* Actualizacion id_ejecutivo_empresa , id_tipo_puesto_ejecutivo_empresa , porcentaje_ejecutivo */
Declare @Fecha_Inicio_Periodo datetime, @Fecha_Fin_Periodo datetime Set @Fecha_Inicio_Periodo = (select Min(id_fecha) from D_FECHA where id_periodo = @Periodo_Produccion) Set @Fecha_Fin_Periodo = (select MAX(id_fecha) from D_FECHA where id_periodo = @Periodo_Produccion)
/* Busca los Ejecutivos de las Empresas que facturan */
select E.NUMERO AS CONVENIO ,isnull(P.numero,0) AS NUMERO_COMERCIAL ,TP.IDTIPOPUESTO , EE.PORCENTAJE, EE.FECHADESDE, EE.FECHAHASTA into #TEMP_EJECUTIVOS_TRAZABILIDAD from HOST03.HOST03.COMERCIAL.PARAMETROS_EMPRESA_EXCEP EE join HOST03.HOST03.COMERCIAL.TIPOS_PUESTOS TP on TP.IDTIPOPUESTO = EE.IDTIPOPUESTO JOIN HOST03.HOST03.COMERCIAL.EMPRESAS E on E.IDEMPRESA = EE.IDEMPRESA left join HOST03.HOST03.COMERCIAL.PERSONAS_PUESTOS PP on PP.IDPERSONA_PUESTO = EE.IDPERSONAPUESTO left join HOST03.HOST03.COMERCIAL.PERSONAS P ON P.IDPERSONA = PP.IDPERSONA left join HOST03.HOST03.COMERCIAL.PUESTOS PU on PU.IDPUESTO = PP.IDPUESTO WHERE E.NUMERO is not null and --P.NUMERO is not null and (EE.FECHAHASTA is null) and TP.IDTIPOPUESTO = 22
/* Actualiza id_ejecutivo_empresa , id_tipo_puesto_ejecutivo_empresa y porcentaje_ejecutivo en H_TRAZABILIDAD_FICHAS */
UPDATE H_TRAZABILIDAD_FICHAS SET id_ejecutivo_empresa = T.NUMERO_COMERCIAL, id_tipo_puesto_ejecutivo_empresa = T.IDTIPOPUESTO, porcentaje_ejecutivo = T.PORCENTAJE
from H_TRAZABILIDAD_FICHAS H Inner join #TEMP_EJECUTIVOS_TRAZABILIDAD T ON H.id_empresa_factura = T.CONVENIO where id_periodo_produccion = @Periodo_Produccion ---- Fecha desde AND FECHADESDE < @Fecha_Fin_Periodo AND ---- Fecha hasta ( FECHAHASTA is null or FECHAHASTA >= @Fecha_Inicio_Periodo)
/*Actualiza con F/D a los casos que no encuentra por id_ejecutivo_empresa */
UPDATE H_TRAZABILIDAD_FICHAS SET id_ejecutivo_empresa= 0, id_tipo_puesto_ejecutivo_empresa = 22, porcentaje_ejecutivo = 0 where id_ejecutivo_empresa is null and id_periodo_produccion = @Periodo_Produccion
/*Actualiza con F/D a los casos que no encuentra por id_tipo_puesto_ejecutivo_empresa */
UPDATE H_TRAZABILIDAD_FICHAS SET id_ejecutivo_empresa= 0, id_tipo_puesto_ejecutivo_empresa = 22, porcentaje_ejecutivo = 0 where id_tipo_puesto_ejecutivo_empresa is null and id_periodo_produccion = @Periodo_Produccion
/*Actualiza con F/D a los casos que no encuentra por porcentaje_ejecutivo */
UPDATE H_TRAZABILIDAD_FICHAS SET id_ejecutivo_empresa= 0, id_tipo_puesto_ejecutivo_empresa = 22, porcentaje_ejecutivo = 0 where porcentaje_ejecutivo is null and id_periodo_produccion = @Periodo_Produccion
